const PLUS_VALUE = 'PLUS_VALUE';
const MINUS_VALUE = 'MINUS_VALUE';

const INITIAL_STATE = {
  value: 0,
  clickCount: 0,
  array: [],
};

function changeValueReducer(state=INITIAL_STATE, action) {
  console.log(action.value, state.array)
  switch (action.type) {
    case PLUS_VALUE:
      return {
        value: state.value + parseInt(action.value),
        clickCount: state.clickCount + 1,
        array: state.array.concat(action.value)
      };
    case MINUS_VALUE:
      return {...state, value: state.value - parseInt(action.value)};
    default:
      return state;
  }
}

export default changeValueReducer;
