import React, { Component } from 'react';
import { connect } from 'react-redux';
import { plus, minus } from '../actions';

class ClickComponent extends Component {
  render() {
    const plusValue = 0;
    const minusValue = 0;
    return (
      <div>
        <div>
          <label htmlFor="plus">Valor à incrementar:</label>
          <input
            type="number"
            value={plusValue}
            onChange={(event) => plusValue =  event.target.value}
            name="plus"
          />
          <button onClick={() => this.props.add(plusValue)}>Incrementar</button>
        </div>
        <div>
          <label htmlFor="minus">Valor à decrementar:</label>
          <input
            type="number"
            value={minusValue}
            onChange={(event) => minusValue =  event.target.value}
            name="minus"
          />
          <button onClick={() => this.props.subtract(minusValue)}>Decrementar</button>
        </div>
        <div>
          <p>Valor atual: <strong>0</strong></p>
        </div>
      </div>
    )
  }
}

const mapDispatchToProps = dispatch => ({
  add: value => dispatch(plus(value)),
  subtract: value => dispatch(minus(value)),

});

export default connect(null, mapDispatchToProps)(ClickComponent);
