import React from 'react';
import redSignal from './images/redSignal.jpeg';
import greenSignal from './images/greenSignal.jpeg';
import yellowSignal from './images/yellowSignal.jpeg';
import * as ActionCreators from './redux/actionCreators'

const renderSignal = (signalColor) => {
  if (signalColor === 'red') {
    return redSignal;
  }
  if (signalColor === 'green') {
    return greenSignal;
  }
  if (signalColor === 'yellow') {
    return yellowSignal;
  }
  return null;
};

export default function TrafficSignal({ signalColor, changeSignal }) {
  return (
    <div>
      <div className="button-container">
        <button type="button" onClick={() => this.props.changeColor(this.redSignal('red'))}>Red</button>
        <button type="button" onClick={() => this.props.changeColor(this.redSignal('yellow'))}>Yellow</button>
        <button type="button" onClick={() => this.props.changeColor(this.redSignal('green'))}>Green</button>
      </div>
      <img className="signal" src={renderSignal(signalColor)} alt="" />
    </div>
  );
}

const mapStateToProps = (state) => ({
  signalColor: state.reducer
});

const mapDispatchToProps = (dispatch) => ({
  changeSignal: color => dispatch(ActionCreators.changeSignal(color))
});
